import React, { useState, useEffect } from 'react';

function DietJournal() {
    const [entries, setEntries] = useState(Array.from({ length: 15 }, () => ({ meal: '', calories: '', category: '', favorite: false, period: 'AM' })));
    const [allMeals, setAllMeals] = useState([]); // State to store all meals from the API
    const categories = ['Breakfast', 'Lunch', 'Dinner', 'Snack'];

    // Fetch all meals from the API
    useEffect(() => {
        fetchAllMeals();
    }, []);

    const fetchAllMeals = async () => {
        try {
            const response = await fetch('https://api.edamam.com/api/recipes/v2?type=any&beta=true&app_id=d0e6a50c&app_key=4039c21decd06beb5d8e7382c1d3dde0%09%E2%80%94');
            if (!response.ok) {
                throw new Error('Failed to fetch meals');
            }
            const data = await response.json();
            setAllMeals(data); // Set the retrieved meals in state
        } catch (error) {
            console.error('Error fetching meals:', error.message);
        }
    };

    const handleEntryChange = (index, event) => {
        const { name, value } = event.target;
        let updatedEntries = [...entries];
        if (name === "meal") {
            const selectedMeal = allMeals.find(meal => meal.name === value);
            if (selectedMeal) {
                updatedEntries[index] = { ...updatedEntries[index], [name]: value, calories: selectedMeal.calories };
            } else {
                updatedEntries[index] = { ...updatedEntries[index], [name]: value, calories: '' };
            }
        } else {
            updatedEntries[index] = { ...updatedEntries[index], [name]: value };
        }
        setEntries(updatedEntries);
    };

    const handleFavoriteChange = (index) => {
        setEntries(prevEntries => {
            const updatedEntries = [...prevEntries];
            updatedEntries[index] = { ...updatedEntries[index], favorite: !updatedEntries[index].favorite };
            return updatedEntries;
        });
    };

    const submitEntry = (index) => {
        const entry = { ...entries[index] };
        entry.dateSubmitted = new Date().toLocaleDateString(); // Current date
        entry.timeSubmitted = new Date().toLocaleTimeString(); // Current time

        try {
            const response = fetch('http://localhost:3000/dietJournalEntries', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(entry),
            });

            if (!response.ok) {
                throw new Error('Failed to submit entry');
            }

            console.log('Entry submitted successfully');
        } catch (error) {
            console.error('Error submitting entry:', error.message);
        }
    };

    const removeEntry = (index) => {
        setEntries(prevEntries => prevEntries.filter((_, i) => i !== index));
    };

    return (
        <div style={{ maxWidth: '600px' }}>
            <h5>Diet Journal</h5>
            <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse', borderSpacing: '0' }}>
                    <thead>
                        <tr>
                            <th style={{ padding: '5px' }}>Meal</th>
                            <th style={{ padding: '5px' }}>Calories</th>
                            <th style={{ padding: '5px' }}>Category</th>
                            <th style={{ padding: '5px' }}>Favorite</th>
                            <th style={{ padding: '5px' }}>AM/PM</th>
                            <th style={{ padding: '5px' }}>Submit</th>
                            <th style={{ padding: '5px' }}>Remove</th>
                        </tr>
                    </thead>
                    <tbody>
                        {entries.map((entry, index) => (
                            <tr key={index}>
                                <td style={{ padding: '5px' }}>
                                    <input
                                        type="text"
                                        name="meal"
                                        value={entry.meal}
                                        onChange={(event) => handleEntryChange(index, event)}
                                        list="mealSuggestions"
                                    />
                                    {/* Display suggestions as the user types */}
                                    <datalist id="mealSuggestions">
                                        {allMeals.map((meal, i) => (
                                            <option key={i} value={meal.name} />
                                        ))}
                                    </datalist>
                                </td>
                                <td style={{ padding: '5px' }}>
                                    <input
                                        type="text"
                                        name="calories"
                                        value={entry.calories}
                                        onChange={(event) => handleEntryChange(index, event)}
                                    />
                                </td>
                                <td style={{ padding: '5px' }}>
                                    <select
                                        name="category"
                                        value={entry.category}
                                        onChange={(event) => handleEntryChange(index, event)}
                                    >
                                        <option value="">Select Category</option>
                                        {categories.map((category, i) => (
                                            <option key={i} value={category}>
                                                {category}
                                            </option>
                                        ))}
                                    </select>
                                </td>
                                <td style={{ padding: '5px' }}>
                                    <input
                                        type="checkbox"
                                        checked={entry.favorite}
                                        onChange={() => handleFavoriteChange(index)}
                                    />
                                </td>
                                <td style={{ padding: '5px' }}>
                                    <select
                                        name="period"
                                        value={entry.period}
                                        onChange={(event) => handleEntryChange(index, event)}
                                    >
                                        <option value="AM">AM</option>
                                        <option value="PM">PM</option>
                                    </select>
                                </td>
                                <td style={{ padding: '5px' }}>
                                    <button onClick={() => submitEntry(index)}>Submit Entry</button>
                                </td>
                                <td style={{ padding: '5px' }}>
                                    <button onClick={() => removeEntry(index)}>Remove</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default DietJournal;
