const App = () => {
  // State for exercise filters
  const [exerciseFilters, setExerciseFilters] = useState({
    muscle: '',
    equipment: '',
    intensity: ''
  });

  // Handler for updating exercise filters
  const handleExerciseFilterChange = (filterName, value) => {
    setExerciseFilters(prevFilters => ({
      ...prevFilters,
      [filterName]: value
    }));
  };

  return (
    <div className="app">
      <Header/>
      {/* Pass exercise filters and filter change handler to ExerciseFilterForm */}
      <ExerciseFilterForm
        selectedMuscle={exerciseFilters.muscle}
        selectedEquipment={exerciseFilters.equipment}
        selectedIntensity={exerciseFilters.intensity}
        onFilterChange={handleExerciseFilterChange}
      />
      {/* Pass exercise filters to ExerciseList */}
      <ExerciseList
        selectedMuscle={exerciseFilters.muscle}
        selectedEquipment={exerciseFilters.equipment}
        selectedIntensity={exerciseFilters.intensity}
      />
      <MealList/>
      <DietJournal/>
      <MealFilterForm/>
    </div>
  );
}

export default App;
