import React, { useState } from 'react';
import ExerciseCard from './ExerciseCard';

function ExerciseList() {
    const [exercises, setExercises] = useState(Array.from({ length: 20 }, () => ({ name: 'Exercise', duration: '30 min' })));
    const [filteredExercises, setFilteredExercises] = useState([...exercises]);
    const [searchTerm, setSearchTerm] = useState('');

    // Function to handle search input change
    const handleSearchChange = (event) => {
        const { value } = event.target;
        setSearchTerm(value);
        filterExercises(value);
    };

    // Function to filter exercises based on search term
    const filterExercises = (searchTerm) => {
        const filtered = exercises.filter(exercise => {
            return exercise.name.toLowerCase().includes(searchTerm.toLowerCase());
        });
        setFilteredExercises(filtered);
    };

    return (
        <div style={{ maxWidth: '400px' }}>
            <h2>Exercise List</h2>
            <div>
                <input 
                    type="text" 
                    placeholder="Search by exercise name" 
                    value={searchTerm} 
                    onChange={handleSearchChange} 
                />
            </div>
            <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                {filteredExercises.map((exercise, index) => (
                    <ExerciseCard key={index} exercise={exercise} name={exercise.name} duration={exercise.duration} />
                ))}
            </div>
        </div>
    );
}

export default ExerciseList;
