import React, { useState, useEffect } from 'react';
import DietJournalEntry from './DietJournalEntry';

function DietJournal() {
    const [entries, setEntries] = useState([]);
    const [predefinedMeals, setPredefinedMeals] = useState([]);

    useEffect(() => {
        // Fetch predefined meals from your API endpoint
        fetch('your_api_endpoint')
            .then(response => response.json())
            .then(data => {
                // Assuming data is an array of meal names
                setPredefinedMeals(data);
            })
            .catch(error => console.error('Error fetching predefined meals:', error));

        // Fetch existing entries from db.json
        fetch('db.json')
            .then(response => response.json())
            .then(data => {
                // Assuming entries are stored in an array under 'entries' key
                setEntries(data.entries);
            })
            .catch(error => console.error('Error fetching entries:', error));
    }, []);

    const handleEntryChange = (index, event) => {
        const { name, value } = event.target;
        const newEntries = [...entries];
        newEntries[index][name] = value;
        setEntries(newEntries);
    };

    const handleRemoveEntry = (index) => {
        const newEntries = [...entries];
        const removedEntry = newEntries.splice(index, 1)[0]; // Remove the entry from the array
        setEntries(newEntries);

        // Send a DELETE request to your API endpoint to remove the entry from the database
        fetch(`your_api_endpoint/${removedEntry.id}`, {
            method: 'DELETE'
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to delete entry');
            }
            return response.json();
        })
        .catch(error => console.error('Error deleting entry:', error));
    };

    return (
        <li>
            <h5>Diet Journal</h5>
            <p>Meal | Calories | Category</p>
            <div style={{ maxHeight: '200px', overflowY: 'auto' }}>
                {entries.map((entry, index) => (
                    <div key={index} style={{ marginBottom: '10px' }}>
                        <select
                            name="meal"
                            value={entry.meal}
                            onChange={(event) => handleEntryChange(index, event)}
                            style={{ marginRight: '10px' }}
                        >
                            <option value="">Select Meal</option>
                            {predefinedMeals.map((meal, i) => (
                                <option key={i} value={meal}>
                                    {meal}
                                </option>
                            ))}
                        </select>
                        <input
                            type="text"
                            name="calories"
                            value={entry.calories}
                            onChange={(event) => handleEntryChange(index, event)}
                            placeholder="Calories"
                            style={{ marginRight: '10px', width: '100px' }}
                        />
                        <input
                            type="text"
                            name="category"
                            value={entry.category}
                            onChange={(event) => handleEntryChange(index, event)}
                            placeholder="Category"
                            style={{ width: '100px' }}
                        />
                        <button onClick={() => handleRemoveEntry(index)}>Remove</button>
                    </div>
                ))}
            </div>
        </li>
    );
}

export default DietJournal;
